package com.lnct;

public class Factorial_Program {
   
   public static void factorial_Program(int n){
	   int fact=1;
    	for(int i=1;i<=5;i++) {
    		fact=fact*i;
    	}
    	System.out.println(fact);
    }
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int n=5;
		  
		factorial_Program(5);

	}

}
